mapper.select:
SELECT CEIL(COUNT(*) / #{row_range}) FROM COMMON_CODE_GROUP


    AND group_id = #{group_id}
    AND group_name = #{group_name}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}
    AND use_yn = #{use_yn}
    AND comment = #{comment}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND group_name LIKE CONCAT('%', #{_group_name}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND comment LIKE CONCAT('%', #{_comment}, '%')

SELECT

    group_id,
    group_name,
    system_create_date,
    system_create_userid,
    system_update_date,
    system_update_userid,
    use_yn,
    comment
FROM COMMON_CODE_GROUP


    AND group_id = #{group_id}
    AND group_name = #{group_name}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}
    AND use_yn = #{use_yn}
    AND comment = #{comment}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND group_name LIKE CONCAT('%', #{_group_name}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND comment LIKE CONCAT('%', #{_comment}, '%')

mapper.insert:
INSERT INTO COMMON_CODE_GROUP(

     group_id 

    , group_name
    , system_create_date
    , system_create_userid
    , system_update_date
    , system_update_userid
    , use_yn
    , comment
)
VALUES(

     #{group_id}

    , #{group_name}
    , #{system_create_date}
    , #{system_create_userid}
    , #{system_update_date}
    , #{system_update_userid}
    , #{use_yn}
    , #{comment}
)

