mapper.select:
SELECT
row_number() over ()AS no,
group_id,
group_name,
group_level,
top_group_id,
use_yn,
system_create_date,
system_create_userid,
system_update_date,
system_update_userid
FROM COMMON_GROUP

    AND group_id=#{group_id}
    AND group_name=#{group_name}
    AND group_level=#{group_level}
    AND top_group_id=#{top_group_id}
    AND use_yn=#{use_yn}
    AND system_create_date=#{system_create_date}
    AND system_create_userid=#{system_create_userid}
    AND system_update_date=#{system_update_date}
    AND system_update_userid=#{system_update_userid}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND group_name LIKE CONCAT('%', #{_group_name}, '%')
    AND group_level LIKE CONCAT('%', #{_group_level}, '%')
    AND top_group_id LIKE CONCAT('%', #{_top_group_id}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')


LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*)/#{row_range}) FROM COMMON_GROUP

    AND group_id=#{group_id}
    AND group_name=#{group_name}
    AND group_level=#{group_level}
    AND top_group_id=#{top_group_id}
    AND use_yn=#{use_yn}
    AND system_create_date=#{system_create_date}
    AND system_create_userid=#{system_create_userid}
    AND system_update_date=#{system_update_date}
    AND system_update_userid=#{system_update_userid}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND group_name LIKE CONCAT('%', #{_group_name}, '%')
    AND group_level LIKE CONCAT('%', #{_group_level}, '%')
    AND top_group_id LIKE CONCAT('%', #{_top_group_id}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

SELECT
group_id,
group_name,
group_level,
top_group_id,
use_yn,
system_create_date,
system_create_userid,
system_update_date,
system_update_userid
FROM COMMON_GROUP

    AND group_id=#{group_id}
    AND group_name=#{group_name}
    AND group_level=#{group_level}
    AND top_group_id=#{top_group_id}
    AND use_yn=#{use_yn}
    AND system_create_date=#{system_create_date}
    AND system_create_userid=#{system_create_userid}
    AND system_update_date=#{system_update_date}
    AND system_update_userid=#{system_update_userid}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND group_name LIKE CONCAT('%', #{_group_name}, '%')
    AND group_level LIKE CONCAT('%', #{_group_level}, '%')
    AND top_group_id LIKE CONCAT('%', #{_top_group_id}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

mapper.insert:
INSERT INTO COMMON_GROUP(
group_id
, group_name
, group_level
, system_create_date
, system_create_userid
, top_group_id
, use_yn
, system_update_date
, system_update_userid
)
VALUES(
#{group_id}
, #{group_name}
, #{group_level}
, NOW()
, #{system_create_userid}
, #{top_group_id}
, #{use_yn}
, #{system_update_date}
, #{system_update_userid}
)

mapper.delete:
DELETE FROM COMMON_GROUP

    AND group_id = #{group_id}
    AND group_name = #{group_name}
    AND group_level = #{group_level}
    AND top_group_id = #{top_group_id}
    AND use_yn = #{use_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}
