SELECT

    menu_code, 
    menu_name_kr, 
    menu_name_en, 
    menu_name_mn, 
    menu_number, 
    menu_level, 
    top_menu_code, 
    url, 
    use_yn, 
    system_create_date, 
    system_create_userid, 
    system_update_date, 
    system_update_userid, 
    menu_sequence, 
    menu_icon, 
    prgm_url 
FROM COMMON_MENU


    AND menu_code = #{menu_code}
    AND menu_name_kr = #{menu_name_kr}
    AND menu_name_en = #{menu_name_en}
    AND menu_name_mn = #{menu_name_mn}
    AND menu_number = #{menu_number}
    AND menu_level = #{menu_level}
    AND top_menu_code = #{top_menu_code}
    AND url = #{url}
    AND use_yn = #{use_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}
    AND menu_sequence = #{menu_sequence}
    AND menu_icon = #{menu_icon}
    AND prgm_url = #{prgm_url}

    AND menu_code LIKE CONCAT('%', #{_menu_code}, '%')
    AND menu_name_kr LIKE CONCAT('%', #{_menu_name_kr}, '%')
    AND menu_name_en LIKE CONCAT('%', #{_menu_name_en}, '%')
    AND menu_name_mn LIKE CONCAT('%', #{_menu_name_mn}, '%')
    AND menu_number LIKE CONCAT('%', #{_menu_number}, '%')
    AND menu_level LIKE CONCAT('%', #{_menu_level}, '%')
    AND top_menu_code LIKE CONCAT('%', #{_top_menu_code}, '%')
    AND url LIKE CONCAT('%', #{_url}, '%')
