    AND menu_sequence LIKE CONCAT('%', #{_menu_sequence}, '%')
    AND menu_icon LIKE CONCAT('%', #{_menu_icon}, '%')
    AND prgm_url LIKE CONCAT('%', #{_prgm_url}, '%')


LIMIT #{row_range} OFFSET ((#{page_no} - 1) * #{row_range})

SELECT CEIL(COUNT(*) / #{row_range}) FROM COMMON_MENU


    AND menu_code = #{menu_code}
