SELECT
ROW_NUMBER() OVER() AS no,
menu_code,
menu_name_kr,
menu_name_en,
menu_name_mn,
menu_number,
menu_level,
top_menu_code,
url,
use_yn,
fn_get_common_code_name('use_yn', use_yn) as use_yn_name,
menu_sequence,
system_create_userid,
system_update_userid,
DATE_FORMAT(system_update_date,'%Y-%m-%d %H:%i:%S') AS system_update_date, -- MariaDB의 DATE_FORMAT 사용
DATE_FORMAT(system_create_date,'%Y-%m-%d %H:%i:%S') AS system_create_date    -- MariaDB의 DATE_FORMAT 사용
FROM COMMON_MENU

    1=1
    AND menu_code = #{menu_code}
    AND menu_name_kr = #{menu_name_kr}
    AND menu_name = #{menu_name_en}
    AND menu_name_mn = #{menu_name_mn}
    AND menu_number = #{menu_number}
    AND menu_level = #{menu_level}
    AND top_menu_code = #{top_menu_code}
    AND url = #{url}
    AND use_yn = #{use_yn}
    AND system_create_userid = #{system_create_userid}
    AND system_update_userid = #{system_update_userid}

SELECT rownum AS no, subquery.* FROM ( SELECT cm.menu_code, cm.menu_name_kr, cm.menu_name_en, cm.menu_name_mn, cm.menu_number, cm.menu_level, cm.top_menu_code, cm.url, cm.use_yn, fn_get_common_code_name('use_yn', cm.use_yn) as use_yn_name, cm.menu_sequence, cm.system_create_userid, cm.system_update_userid, TO_DATE(TO_CHAR(cm.system_update_date, 'yyyy-MM-dd HH24:MI:SS'), 'yyyy-MM-dd HH24:MI:SS') AS system_update_date, TO_DATE(TO_CHAR(cm.system_create_date, 'yyyy-MM-dd HH24:MI:SS'), 'yyyy-MM-dd HH24:MI:SS') AS system_create_date, LISTAGG(group_info, ',') WITHIN GROUP (ORDER BY group_level, group_id) AS access_right_group FROM COMMON_MENU cm LEFT JOIN ( SELECT DISTINCT cagml.MENU_CODE, CASE WHEN cg.GROUP_LEVEL = 1 THEN '{"group_id":"' || cg.GROUP_ID || '",' || '"group_name":"' || cg.GROUP_NAME || '",' || '"top_group_id":"' || cg.TOP_GROUP_ID || '",' || '"group_level":"' || cg.GROUP_LEVEL || '"}' ELSE '{"group_id":"' || cg.GROUP_ID || '",' || '"group_name":"' || cg.GROUP_NAME || '",' || '"top_group_id":"' || cg.TOP_GROUP_ID || '",' || '"group_level":"' || cg.GROUP_LEVEL || '"}' END AS group_info, cg.GROUP_LEVEL, cg.GROUP_ID FROM COMMON_ACCESS_GROUP_MENU_LIST cagml JOIN COMMON_GROUP cg ON cagml.ACCESS_RIGHTS_GROUP_ID = cg.GROUP_ID UNION SELECT DISTINCT cagml.MENU_CODE, '{"group_id":"' || cg_top.GROUP_ID || '",' || '"group_name":"' || cg_top.GROUP_NAME || '",' || '"top_group_id":"' || cg_top.TOP_GROUP_ID || '",' || '"group_level":"' || cg_top.GROUP_LEVEL || '"}' AS group_info, cg_top.GROUP_LEVEL, cg_top.GROUP_ID FROM COMMON_ACCESS_GROUP_MENU_LIST cagml JOIN COMMON_GROUP cg ON cagml.ACCESS_RIGHTS_GROUP_ID = cg.GROUP_ID JOIN COMMON_GROUP cg_top ON cg.TOP_GROUP_ID = cg_top.GROUP_ID WHERE cg.GROUP_LEVEL = 1 AND cg_top.GROUP_LEVEL = 0 ) access_groups ON cm.menu_code = access_groups.MENU_CODE
1=1 AND cm.menu_code = #{menu_code} AND cm.menu_name_kr = #{menu_name_kr} AND cm.menu_name_en = #{menu_name_en} AND cm.menu_name_mn = #{menu_name_mn} AND cm.menu_number = #{menu_number} AND cm.menu_level = #{menu_level} AND cm.top_menu_code = #{top_menu_code} AND cm.url = #{url} AND cm.use_yn = #{use_yn} AND cm.system_create_userid = #{system_create_userid} AND cm.system_update_userid = #{system_update_userid}
GROUP BY cm.menu_code, cm.menu_name, cm.menu_number, cm.menu_level, cm.top_menu_code, cm.url, cm.use_yn, cm.menu_sequence, cm.system_create_userid, cm.system_update_userid, cm.system_update_date, cm.system_create_date ORDER BY cm.menu_sequence ASC, cm.top_menu_code ASC, cm.menu_number ASC, cm.top_menu_code ASC ) subquery

