mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY system_create_date DESC )AS no,

        config_group_id, 
        config_group_name, 
        use_yn, 
        system_create_date, 
        system_create_userid, 
        system_update_date, 
        system_update_userid 
    FROM COMMON_SITE_CONFIG_GROUP


        AND config_group_id = #{config_group_id}
        AND config_group_name = #{config_group_name}
        AND use_yn = #{use_yn}
        AND system_create_date = #{system_create_date}
        AND system_create_userid = #{system_create_userid}
        AND system_update_date = #{system_update_date}
        AND system_update_userid = #{system_update_userid}

        AND config_group_id LIKE CONCAT('%', #{_config_group_id}, '%')
        AND config_group_name LIKE CONCAT('%', #{_config_group_name}, '%')
        AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
        AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
        AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
        AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
        AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM COMMON_SITE_CONFIG_GROUP


    AND config_group_id = #{config_group_id}
    AND config_group_name = #{config_group_name}
    AND use_yn = #{use_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND config_group_id LIKE CONCAT('%', #{_config_group_id}, '%')
    AND config_group_name LIKE CONCAT('%', #{_config_group_name}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

SELECT

    config_group_id,
    config_group_name,
    use_yn,
    system_create_date,
    system_create_userid,
    system_update_date,
    system_update_userid
FROM COMMON_SITE_CONFIG_GROUP


    AND config_group_id = #{config_group_id}
    AND config_group_name = #{config_group_name}
    AND use_yn = #{use_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND config_group_id LIKE CONCAT('%', #{_config_group_id}, '%')
    AND config_group_name LIKE CONCAT('%', #{_config_group_name}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

mapper.insert:
INSERT INTO COMMON_SITE_CONFIG_GROUP(

     config_group_id 
    , config_group_name 
    , use_yn 
    , system_create_date 
    , system_create_userid 

    , system_update_date
    , system_update_userid
)
VALUES(

     #{config_group_id}
    , #{config_group_name}
    , #{use_yn}
    , #{system_create_date}
    , #{system_create_userid}

    , #{system_update_date}
    , #{system_update_userid}
)

