mapper.select:
SELECT
id,
group_id,
user_email,
user_id,
office_code,
FN_GET_OFFICE_NAME(office_code) office_name,
system_create_date,
system_create_userid,
system_update_date,
system_update_userid
FROM COMMON_USER_GROUP

    AND id=#{id}
    AND group_id=#{group_id}
    AND user_email=#{user_email}
    AND user_id=#{user_id}
    AND office_code=#{office_code}
    AND system_create_date=#{system_create_date}
    AND system_create_userid=#{system_create_userid}
    AND system_update_date=#{system_update_date}
    AND system_update_userid=#{system_update_userid}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND user_email LIKE CONCAT('%', #{_user_email}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')


LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*)/#{row_range}) FROM COMMON_USER_GROUP

    AND id=#{id}
    AND group_id=#{group_id}
    AND user_email=#{user_email}
    AND user_id=#{user_id}
    AND office_code=#{office_code}
    AND system_create_date=#{system_create_date}
    AND system_create_userid=#{system_create_userid}
    AND system_update_date=#{system_update_date}
    AND system_update_userid=#{system_update_userid}

    AND group_id LIKE CONCAT('%', #{_group_id}, '%')
    AND user_email LIKE CONCAT('%', #{_user_email}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

SELECT id, group_id, (SELECT group_name FROM COMMON_GROUP b where b.group_id = a.group_id) as group_name, (SELECT user_name FROM COMMON_USER c where c.id = a.id) as user_name, user_email, user_id, office_code, system_create_date, system_create_userid, system_update_date, system_update_userid FROM COMMON_USER_GROUP a
AND id=#{id} AND group_id=#{group_id} AND user_email=#{user_email} AND user_id=#{user_id} AND office_code=#{office_code} AND system_create_date=#{system_create_date} AND system_create_userid=#{system_create_userid} AND system_update_date=#{system_update_date} AND system_update_userid=#{system_update_userid}
AND id LIKE CONCAT('%', #{_id}, '%') AND group_id LIKE CONCAT('%', #{_group_id}, '%') AND user_email LIKE CONCAT('%', #{_user_email}, '%') AND user_id LIKE CONCAT('%', #{_user_id}, '%') AND office_code LIKE CONCAT('%', #{_office_code}, '%') AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%') AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%') AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%') AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

mapper.insert:
INSERT INTO COMMON_USER_GROUP(
id
,group_id
, user_email
, user_id
, office_code
, system_create_date
, system_create_userid
, system_update_date
, system_update_userid
)
VALUES(
#{id}
,#{group_id}
, #{user_email}
, #{user_id}
, #{office_code}
, NOW()
, #{system_create_userid}
, #{system_update_date}
, #{system_update_userid}
)

mapper.delete:
DELETE FROM COMMON_USER_GROUP

    AND id = #{id}
    AND group_id = #{group_id}
    AND user_email = #{user_email}
    AND user_id = #{user_id}
    AND office_code = #{office_code}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

mapper.update:
UPDATE COMMON_USER_GROUP
user_email = #{user_email}, user_id=#{user_id}, office_code=#{office_code}, system_create_date=#{system_create_date}, system_create_userid=#{system_create_userid}, system_update_date=#{system_update_date}, system_update_userid=#{system_update_userid},
id = #{id} AND group_id = #{group_id}

mapper.select:
SELECT
a.id,
a.email,
a.password,
a.role,
a.user_id,
a.office_code
FROM COMMON_USER a

    NOT EXISTS (
    SELECT 1
    FROM COMMON_USER_GROUP b
    WHERE a.user_id = b.user_id
    AND b.group_id = #{group_id}
    )
    AND a.id=#{id}
    AND a.email=#{email}
    AND a.password=#{password}
    AND a.role=#{role}
    AND a.user_id=#{user_id}
    AND a.office_code=#{office_code}

    AND a.id LIKE CONCAT('%', #{_id}, '%')
    AND a.email LIKE CONCAT('%', #{_email}, '%')
    AND a.password LIKE CONCAT('%', #{_password}, '%')
    AND a.role LIKE CONCAT('%', #{_role}, '%')
    AND a.user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND a.office_code LIKE CONCAT('%', #{_office_code}, '%')

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*)/#{row_range}) FROM COMMON_USER a

    NOT EXISTS (
    SELECT 1
    FROM COMMON_USER_GROUP b
    WHERE a.user_id = b.user_id
    AND b.group_id = #{group_id}
    )
    AND a.id=#{id}
    AND a.email=#{email}
    AND a.password=#{password}
    AND a.role=#{role}
    AND a.user_id=#{user_id}
    AND a.office_code=#{office_code}

    AND a.id LIKE CONCAT('%', #{_id}, '%')
    AND a.email LIKE CONCAT('%', #{_email}, '%')
    AND a.password LIKE CONCAT('%', #{_password}, '%')
    AND a.role LIKE CONCAT('%', #{_role}, '%')
    AND a.user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND a.office_code LIKE CONCAT('%', #{_office_code}, '%')
