mapper.select:
SELECT
    row_number() over ()AS no,

    id, 
    email, 
    password, 
    role, 
    user_id, 
    office_code,
    FN_GET_OFFICE_NAME(office_code) AS office_name,
    system_create_userid, 
    system_create_date, 
    system_update_userid, 
    system_update_date, 
    user_name, 
    tel, 
    address, 
    address_detail, 
    postal_code, 
    uuid 
FROM COMMON_USER


    AND id = #{id}
    AND email = #{email}
    AND password = #{password}
    AND role = #{role}
    AND user_id = #{user_id}
    AND office_code = #{office_code}
    AND system_create_userid = #{system_create_userid}
    AND system_create_date = #{system_create_date}
    AND system_update_userid = #{system_update_userid}
    AND system_update_date = #{system_update_date}
    AND user_name = #{user_name}
    AND tel = #{tel}
    AND address = #{address}
    AND address_detail = #{address_detail}
    AND postal_code = #{postal_code}
    AND uuid = #{uuid}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND password LIKE CONCAT('%', #{_password}, '%')
    AND role LIKE CONCAT('%', #{_role}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND user_name LIKE CONCAT('%', #{_user_name}, '%')
    AND tel LIKE CONCAT('%', #{_tel}, '%')
    AND address LIKE CONCAT('%', #{_address}, '%')
    AND address_detail LIKE CONCAT('%', #{_address_detail}, '%')
    AND postal_code LIKE CONCAT('%', #{_postal_code}, '%')
    AND uuid LIKE CONCAT('%', #{_uuid}, '%')


LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM COMMON_USER


    AND id = #{id}
    AND email = #{email}
    AND password = #{password}
    AND role = #{role}
    AND user_id = #{user_id}
    AND office_code = #{office_code}
    AND system_create_userid = #{system_create_userid}
    AND system_create_date = #{system_create_date}
    AND system_update_userid = #{system_update_userid}
    AND system_update_date = #{system_update_date}
    AND user_name = #{user_name}
    AND tel = #{tel}
    AND address = #{address}
    AND address_detail = #{address_detail}
    AND postal_code = #{postal_code}
    AND uuid = #{uuid}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND password LIKE CONCAT('%', #{_password}, '%')
    AND role LIKE CONCAT('%', #{_role}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND user_name LIKE CONCAT('%', #{_user_name}, '%')
    AND tel LIKE CONCAT('%', #{_tel}, '%')
    AND address LIKE CONCAT('%', #{_address}, '%')
    AND address_detail LIKE CONCAT('%', #{_address_detail}, '%')
    AND postal_code LIKE CONCAT('%', #{_postal_code}, '%')
    AND uuid LIKE CONCAT('%', #{_uuid}, '%')

